import React, { useState } from 'react'

const Categories = ({ categories, filterItems }) => {
  return (
    <div className='btn-container'>
      {categories.map((category) => {
        return (
          <div className='filter-btn' onClick={() => filterItems('category')}>
            {category}
          </div>
        )
      })}
    </div>
  )
}

export default Categories
