const url = 'https://course-api.com/react-tabs-project'
function App() {
  const [isLoading, setIsLoading] = useState(true)

  const getData = async () => {
    try {
      const response = await fetch(url)
      const data = await response.json()
      isLoading(false)
    } catch (error) {
      console.log(error)
      setIsLoading(true)
    }
  }

  if (isLoading) {
    return <h2>Loading......</h2>
  }
}

export default App
