import React, { useState, useEffect } from 'react'
import { FaAngleDoubleRight, FaCentercode } from 'react-icons/fa'
// ATTENTION!!!!!!!!!!
// I SWITCHED TO PERMANENT DOMAIN
const url = 'https://course-api.com/react-tabs-project'

function App() {
  const [isLoading, setIsLoading] = useState(true)
  const [jobs, setJobs] = useState([])
  const [value, setValue] = useState(0)

  const getData = async () => {
    try {
      const response = await fetch(url)
      const data = await response.json()
      console.log(data)
      setIsLoading(false)
      setJobs(data)
    } catch (error) {
      console.log(error)
      setIsLoading(true)
    }
  }

  useEffect(() => {
    getData()
  }, [])

  if (isLoading) {
    return <h2 className='section loading'>Loading......</h2>
  } else {
    return (
      <section>
        <div>
          <h2 className='title'>Experience</h2>
          <div className='underline'></div>
        </div>
        {jobs.map((job) => {
          const { id, title, dates, duties, company, order } = job

          return (
            <section className='jobs-center'>
              <button
                className='btn-container'
                onClick={() => setValue(job.order)}
              >
                {job.company}
              </button>
            </section>
          )
        })}

        {jobs.filter((job) => {
          const { id, title, dates, duties, company, order } = job
          if (job.order === value) {
            return (
              <section className='job-center'>
                <h3>{title}</h3>
              </section>
            )
          }
        })}
      </section>
    )
  }
}

export default App
